mapper.select:
SELECT a.id, a.create_by, a.create_time, a.delete_flag, a.update_by, a.update_time, a.url, a.username, a.password, a.project_address, a.package_path_id, a.temp_code, a.database_type, a.user_id, a.data_source_name, a.driver_name, a.apply_status FROM springboot.db_base_setting a LEFT JOIN springboot.user b ON a.user_id = b.id WHERE a.delete_flag = '0'
AND a.username LIKE CONCAT('%',#{username},'%')
AND a.user_id = #{userId}
AND a.apply_status = #{applyStatus}
AND a.database_type = #{databaseType}
AND a.url LIKE CONCAT('%',#{url},'%')
LIMIT #{offset}, #{pageNumber}

SELECT count(*)
FROM springboot.db_base_setting a
LEFT JOIN springboot.user b
ON a.user_id = b.id
WHERE a.delete_flag = '0'

    AND a.username LIKE CONCAT('%',#{username},'%')


    AND a.user_id = #{userId}


    AND a.apply_status = #{applyStatus}


    AND a.database_type = #{databaseType}


    AND a.url LIKE CONCAT('%',#{url},'%')
