mapper.select:
SELECT temp.userId FROM
(SELECT a.user_b_id AS userId FROM u_u a
WHERE a.user_a_id = #{userId}
AND a.type = #{type}

UNION
SELECT b.user_a_id AS userId FROM u_u b
WHERE b.user_b_id = #{userId}
AND b.type = #{type}

)temp

SELECT *
FROM springboot.u_u u
WHERE u.delete_flag = 0
  AND (
        (u.user_a_id = #{id1}
            AND
         u.user_b_id = #{id2}
            )
        OR (
            (u.user_a_id = #{id2}
                AND
             u.user_b_id = #{id1}
                )
            )
    )
